create table product
(
    id       int         null,
    name     varchar(20) null,
    sort     varchar(20) null,
    price    varchar(20) null,
    oneprice varchar(20) null,
    img      varchar(20) null,
    sale     varchar(20) null,
    face     varchar(20) null,
    body     varchar(20) null,
    length   varchar(20) null,
    quantity varchar(20) null,
    source   varchar(20) null
);

